import React, { useState } from 'react';
import Papa from 'papaparse';
import axios from 'axios';

const flattenData = (data, prefix = '') => {
  const flattenedData = {};

  const flattenObject = (obj, parentKey = '') => {
    for (const key in obj) {
      const value = obj[key];
      const newKey = parentKey ? `${parentKey}_${key}` : key;

      if (typeof value === 'object' && value !== null) {
        // Recursively flatten nested objects
        flattenObject(value, newKey);
      } else {
        flattenedData[newKey] = value;
      }
    }
  };

  try {
    const parsedData = JSON.parse(data);
    flattenObject(parsedData, prefix);
  } catch (error) {
    console.warn('Error parsing JSON in flattenData for data:', data, 'Error:', error);
    // Treat the data as a simple key-value pair
    const [key, value] = data.split(':').map((item) => item.trim());
    const newKey = prefix ? `${prefix}_${key}` : key;
    flattenedData[newKey] = value;
  }

  return flattenedData;
};

const mergeRow = (originalRow) => {
  const newRow = { ...originalRow };

  for (const column in originalRow) {
    let jsonData;
    try {
      jsonData = JSON.parse(originalRow[column]);
    } catch (error) {
      jsonData = null;
    }

    if (jsonData && typeof jsonData === 'object') {
      const flattenedColumnData = flattenData(originalRow[column], column);
      for (const key in flattenedColumnData) {
        newRow[key] = flattenedColumnData[key];
      }
      delete newRow[column]; // Remove the original column containing JSON data
    }
  }

  return newRow;
};

const CSVProcessor = () => {
  const [inputFile, setInputFile] = useState(null);
  const [outputFileLink, setOutputFileLink] = useState(null);

  const processCSV = async () => {
    if (!inputFile) return;

    const reader = new FileReader();
    reader.onload = async (e) => {
      const csvData = e.target.result;
      const rows = Papa.parse(csvData, { header: true }).data;

      const uniqueRows = new Set();
      const outputRows = [];

      for (const row of rows) {
        const newRow = mergeRow({}, row);
        const rowString = JSON.stringify(newRow);
        if (!uniqueRows.has(rowString)) {
          uniqueRows.add(rowString);
          outputRows.push(newRow);
        }
      }

      const csvOutput = Papa.unparse(outputRows);
      const blob = new Blob([csvOutput], { type: 'text/csv' });
      const url = URL.createObjectURL(blob);
      setOutputFileLink(url);
    };

    reader.readAsText(inputFile);
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setInputFile(file);
  };

  const downloadOutputCSV = () => {
    if (outputFileLink) {
      axios.get(outputFileLink, { responseType: 'blob' }).then((res) => {
        const url = window.URL.createObjectURL(new Blob([res.data]));
        const link = document.createElement('a');
        link.href = url;
        link.setAttribute('download', 'output.csv');
        document.body.appendChild(link);
        link.click();
      });
    }
  };

  return (
    <div>
      <h1>CSV Processor</h1>
      <input type="file" onChange={handleFileChange} />
      <button onClick={processCSV}>Process CSV</button>
      {outputFileLink && (
        <div>
          <p>CSV file has been flattened!</p>
          <button onClick={downloadOutputCSV}>Download Output CSV</button>
        </div>
      )}
    </div>
  );
};

export { flattenData, mergeRow };
