export function flattenJSON(jsonData, propertiesKey = "PROPERTIES") {
  const flattenedData = [];

  function flattenObject(obj, prefix = "") {
    for (const key in obj) {
      if (obj.hasOwnProperty(key)) {
        const value = obj[key];
        if (typeof value === "object" && value !== null && !Array.isArray(value)) {
          flattenObject(value, prefix + key + "_");
        } else if (Array.isArray(value)) {
          value.forEach((item, index) => {
            if (typeof item === "object" && item !== null) {
              flattenObject(item, `${prefix}${key}[${index}].`);
            } else {
              const newKey = `${prefix}${key}[${index}]`;
              flattenedData.push({ [newKey]: item });
            }
          });
        } else {
          const newKey = prefix + key;
          flattenedData.push({ [newKey]: value });
        }
      }
    }
  }

  if (Array.isArray(jsonData)) {
    jsonData.forEach((item) => {
      if (item.hasOwnProperty(propertiesKey) && typeof item[propertiesKey] === "object") {
        const propertiesData = item[propertiesKey];
        delete item[propertiesKey];
        flattenObject(item);
        for (const key in propertiesData) {
          if (propertiesData.hasOwnProperty(key)) {
            const value = propertiesData[key];
            flattenedData.push({ [propertiesKey + "_" + key]: value });
          }
        }
      } else {
        flattenObject(item);
      }
    });
  } else if (typeof jsonData === "object") {
    // Single JSON object
    if (jsonData.hasOwnProperty(propertiesKey) && typeof jsonData[propertiesKey] === "object") {
      const propertiesData = jsonData[propertiesKey];
      delete jsonData[propertiesKey];
      flattenObject(jsonData);
      for (const key in propertiesData) {
        if (propertiesData.hasOwnProperty(key)) {
          const value = propertiesData[key];
          flattenedData.push({ [propertiesKey + "_" + key]: value });
        }
      }
    } else {
      flattenObject(jsonData);
    }
  }

  return flattenedData;
}

export function flattenJSONColumns(data, renamedHeaders) {
  const flattenedData = [];
  const flattenedHeaders = [];
  const flattenedRows = [];

  function flattenObject(obj, prefix = "") {
    for (const key in obj) {
      if (obj.hasOwnProperty(key)) {
        const value = obj[key];
        if (typeof value === "object" && value !== null && !Array.isArray(value)) {
          flattenObject(value, prefix + key + ".");
        } else if (Array.isArray(value)) {
          value.forEach((item, index) => {
            if (typeof item === "object" && item !== null) {
              flattenObject(item, `${prefix}${key}[${index}].`);
            } else {
              const newKey = `${prefix}${key}[${index}]`;
              flattenedHeaders.push({ [newKey]: renamedHeaders[key] || key });
              flattenedRows.push({ [newKey]: item });
            }
          });
        } else {
          const newHeader = renamedHeaders[key] || key;
          flattenedHeaders.push({ [newHeader]: newHeader });
          flattenedRows.push({ [newHeader]: value });
        }
      }
    }
  }

  data.forEach((row) => {
    const newRow = {};
    for (const header in row) {
      if (row.hasOwnProperty(header)) {
        const value = row[header];
        if (typeof value === "object" && value !== null) {
          flattenObject(value, header + ".");
        } else {
          const newHeader = renamedHeaders[header] || header;
          newRow[newHeader] = value;
        }
      }
    }
    flattenedRows.push(newRow);
  });

  flattenedData.push(...flattenedRows, ...flattenedHeaders); // Original data followed by flattened data
