import Papa from "papaparse";
import DataTable from "react-data-table-component";
import { useDropzone } from "react-dropzone";
import Modal from "react-modal";
import RowPopup from "./RowPopup";
import "./App.css";

Modal.setAppElement("#root");

const App = () => {
  const [data, setData] = useState([]);
  const [headers, setHeaders] = useState([]);
  const [renamedHeaders, setRenamedHeaders] = useState({});
  const [hiddenColumns, setHiddenColumns] = useState([]);
  const [filterCriteria, setFilterCriteria] = useState({});
  const [searchTerms, setSearchTerms] = useState({});
  const [dropdownOptions, setDropdownOptions] = useState({});
  const [initialState, setInitialState] = useState({});
  const [groupByColumns, setGroupByColumns] = useState({});
  const [selectedRowData, setSelectedRowData] = useState({});
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [groupedData, setGroupedData] = useState([]);

  useEffect(() => {
    // Update filter criteria with renamed headers and handle hiddenColumns as an array
    setFilterCriteria((prevCriteria) => {
      const updatedCriteria = {};
      headers.forEach((header) => {
        const newColumn = renamedHeaders[header] || header;
        if (!hiddenColumns.includes(newColumn)) {
          updatedCriteria[newColumn] = prevCriteria[header];
        }
      });
      return updatedCriteria;
    });
  }, [renamedHeaders, hiddenColumns, headers, filterCriteria]);

  useEffect(() => {
    setDropdownOptions((prevOptions) => {
      const updatedOptions = { ...prevOptions };
      headers.forEach((header) => {
        if (!hiddenColumns.includes(header)) {
          const searchTerm = searchTerms[header]?.toLowerCase();
          const allValues = Array.from(new Set(data.map((row) => row[header])));
          const filteredValues = allValues.filter(
            (value) => !searchTerm || value.toLowerCase().includes(searchTerm)
          );
          updatedOptions[header] = filteredValues;
        }
      });
      return updatedOptions;
    });
  }, [searchTerms, data, headers, hiddenColumns]);

  useEffect(() => {
    setInitialState({
      renamedHeaders: { ...renamedHeaders },
      hiddenColumns: [...hiddenColumns],
      filterCriteria: { ...filterCriteria },
      searchTerms: { ...searchTerms },
    });
  }, [renamedHeaders, hiddenColumns, filterCriteria, searchTerms]);

  useEffect(() => {
    if (!data || data.length === 0) {
      // If data is not available or empty, do nothing
      return;
    }

    const filteredData = data.filter((row) =>
      headers.every((header) => {
        const criteria = filterCriteria[header];
        const searchTerm = searchTerms[header];
        return (
          (!criteria || criteria === "All" || row[header] === criteria) &&
          (!searchTerm || row[header].toLowerCase().includes(searchTerm.toLowerCase()))
        );
      })
    );

    // Apply group by and sort logic
    const groupedAndSortedData = groupAndSortTableData(filteredData);
    setGroupedData(groupedAndSortedData.map(row => ({ ...row }))); // Make sure each row is an object
  }, [data, headers, filterCriteria, searchTerms]);


  const handleDrop = useCallback(async (acceptedFiles) => {
    const file = acceptedFiles[0];
    const reader = new FileReader();

    reader.onload = async (event) => {
      const fileContent = event.target.result;
      if (file.name.endsWith(".csv")) {
        try {
          const flattenedData = await processCSVData(fileContent);
          setData(flattenedData.data);
          setHeaders(flattenedData.headers);
          // ... (rest of the state updates)
        } catch (error) {
          alert("Error processing CSV data: " + error.message);
        }
      } else if (file.name.endsWith(".json")) {
        // Handle JSON data directly as before
        try {
          const jsonData = JSON.parse(fileContent);
          setData(jsonData);
          setHeaders(Object.keys(jsonData[0]));
          // ... (rest of the state updates)
        } catch (error) {
          alert("Error parsing JSON data: " + error.message);
        }
      } else {
        alert("Unsupported file format. Please upload either CSV or JSON file.");
      }
    };

    reader.readAsText(file);
  }, []);


  const processCSVData = async (data) => {
    let flattenedData;
    if (Array.isArray(data)) {
      // JSON data is already in the correct format
      flattenedData = flattenJSON(data);
    } else {
      // CSV data needs to be processed using PapaParse and then flattened
      const parsedData = await Papa.parse(data, {
        header: true,
        skipEmptyLines: true,
      });
      flattenedData = flattenCSVData(parsedData.data);
    }

    return {
      data: flattenedData,
      headers: Object.keys(flattenedData[0]),
    };
  };


  const flattenCSVData = (data) => {
    const flattenRow = (row, prefix = "") => {
      let flattenedRow = {};
      for (const [key, value] of Object.entries(row)) {
        const newKey = prefix ? `${prefix}_${key}` : key;
        if (typeof value === "string" && value.startsWith("{")) {
          try {
            const parsedValue = JSON.parse(value);
            if (typeof parsedValue === "object" && !Array.isArray(parsedValue) && parsedValue !== null) {
              // If the value is a nested JSON object, recursively flatten it
              const nestedFlattenedRow = flattenRow(parsedValue, newKey);
              flattenedRow = { ...flattenedRow, ...nestedFlattenedRow };
            } else {
              // If the value is a JSON array or scalar, use the value directly
              flattenedRow[newKey] = parsedValue;
            }
          } catch (error) {
            // If JSON parsing fails, leave the original value as is
            flattenedRow[newKey] = value;
          }
        } else {
          // If the value is not a JSON object or JSON array, use the value directly
          flattenedRow[newKey] = value;
        }
      }
      return flattenedRow;
    };

    return data.map((row) => flattenRow(row));
  };

  // Helper function to flatten JSON properties
  const flattenJSON = (obj, prefix = "") => {
    return Object.keys(obj).reduce((acc, key) => {
      const newKey = prefix ? `${prefix}_${key}` : key;
      if (typeof obj[key] === "object" && !Array.isArray(obj[key]) && obj[key] !== null) {
        Object.assign(acc, flattenJSON(obj[key], newKey));
      } else {
        acc[newKey] = obj[key];
      }
      return acc;
    }, {});
  };

  // Function to group and sort table data
  const groupAndSortTableData = (tableData) => {
    if (!groupByColumns || Object.keys(groupByColumns).length === 0 || tableData.length === 0) {
      return tableData;
    }

    const flattenedData = tableData.map((row) => flattenJSON(row)); // Flatten each row

    const groups = {};
    flattenedData.forEach((row) => {
      const groupKey = Object.entries(groupByColumns)
        .map(([column, selected]) => (selected ? row[column] : ""))
        .join("-");
      if (!groups[groupKey]) {
        groups[groupKey] = [];
      }
      groups[groupKey].push(row);
    });

    // Sort the data based on the groupBy column and any additional sorting criteria
    const sortedGroups = Object.values(groups).map((group) =>
      group.sort((a, b) => {
        const sortByColumn = Object.entries(groupByColumns).find(([column, selected]) => selected);
        if (sortByColumn) {
          const [sortBy, _] = sortByColumn;
          const aValue = a[sortBy];
          const bValue = b[sortBy];
          if (!hiddenColumns.includes(sortBy) && aValue !== undefined && bValue !== undefined) {
            return aValue.localeCompare(bValue);
          }
        }
        return 0;
      })
    );

    return sortedGroups.flat().map((row) => ({ ...row })); // Flatten the array of arrays into a single array and format each row as an object
  };


  const { getRootProps, getInputProps } = useDropzone({
    onDrop: handleDrop,
    accept: ".csv, .json", // Allow both CSV and JSON files
    multiple: false,
  });

  const groupByColumn = "groupby";
  const updatedHeaders = [...headers.slice(0, 1), groupByColumn, ...headers.slice(1)];

  const columns = updatedHeaders.map((header) => ({
    name: renamedHeaders[header] || header,
    selector: header,
    sortable: true,
    wrap: true,
    format: (row) => {
      const value = row[header];
      if (value && value.length > 100) {
        return value.substring(0, 100) + "...";
      }
      return value;
    },
    omit: header === groupByColumn || hiddenColumns.includes(header),
    grow: 1,
  }));

  const handleRowClick = (row) => {
    setSelectedRowData(row);
    setIsModalOpen(true);
  };

  const handleColumnToggle = (event, column) => {
    const isChecked = event.target.checked;
    setHiddenColumns((prevHiddenColumns) => {
      if (isChecked) {
        // If column is checked, remove it from hiddenColumns array
        return prevHiddenColumns.filter((hiddenColumn) => hiddenColumn !== column);
      } else {
        // If column is unchecked, add it to hiddenColumns array
        return [...prevHiddenColumns, column];
      }
    });
    setRenamedHeaders((prevRenamedHeaders) => {
      const updatedHeaders = { ...prevRenamedHeaders };
      if (!isChecked) {
        delete updatedHeaders[column];
      }
      return updatedHeaders;
    });
  };

  const handleGroupByToggle = (event, column) => {
    const isChecked = event.target.checked;
    setGroupByColumns((prevGroupByColumns) => ({
      ...prevGroupByColumns,
      [column]: isChecked,
    }));
  };

  // Define missing functions
  const handleReset = () => {
    setRenamedHeaders(initialState.renamedHeaders);
    setHiddenColumns(initialState.hiddenColumns);
    setFilterCriteria(initialState.filterCriteria);
    setSearchTerms(initialState.searchTerms);
    setGroupByColumns({});
  };

  const [isFileNameModalOpen, setIsFileNameModalOpen] = useState(false);
  const [selectedFileFormat, setSelectedFileFormat] = useState("");
  const [fileName, setFileName] = useState("");

  const promptFileName = (format) => {
    setSelectedFileFormat(format);
    setIsFileNameModalOpen(true);
  };

  const handleFileNameSubmit = () => {
    setIsFileNameModalOpen(false);
    if (fileName.trim() !== "") {
      handleDownload(selectedFileFormat, fileName);
    }
  };

  const handleDownload = (format, fileName) => {
    if (groupedData.length === 0) {
      alert("No data to download.");
      return;
    }

    const flattenedData = groupedData.map((row) => flattenJSON(row)); // Flatten each row

    let dataToExport, fileExtension;
    if (format === "csv") {
      dataToExport = Papa.unparse(flattenedData, { header: true });
      fileExtension = "csv";
    } else if (format === "json") {
      dataToExport = JSON.stringify(flattenedData, null, 2);
      fileExtension = "json";
    }

    const blob = new Blob([dataToExport], { type: `text/${fileExtension};charset=utf-8;` });
    const link = document.createElement("a");
    if (link.download !== undefined) {
      const url = URL.createObjectURL(blob);
      link.setAttribute("href", url);
      link.setAttribute("download", `${fileName}.${fileExtension}`);
      link.style.visibility = "hidden";
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    }
  };


  return (
    <div className="App">
      {/* Section 1: Header or Title - CVS Table Display */}
      <div className="section section1">
        <h1>CVS | JSON Viewer</h1>
      </div>

      {/* Section 2: CVS File Input */}
      <div className="section section2">
        <div className="upload-container">
          <h2>Upload CSV|JSON Formatted File</h2>
          <div {...getRootProps()} className="dropzone">
            <input {...getInputProps()} />
            <p>Drag 'n' drop a CSV or JSON file here, or click to select a file</p>
          </div>
        </div>
      </div>

      {/* Section 3: Toggle Section */}
      <div className="section toggle-section">
        <div className="toggle-table-container">
          <table className="toggle-columns-table">
            <tbody>
              {headers.map((header) => (
                <tr key={header}>
                  <td>
                    <input
                      type="checkbox"
                      checked={!hiddenColumns.includes(header)}
                      onChange={(e) => handleColumnToggle(e, header)}
                    />
                  </td>
                  <td className="field-name-cell">
                    <input
                      type="text"
                      value={renamedHeaders[header] || header}
                      onChange={(e) =>
                        setRenamedHeaders((prevRenamedHeaders) => ({
                          ...prevRenamedHeaders,
                          [header]: e.target.value,
                        }))
                      }
                      maxLength={100}
                      style={{ width: "98%" }} // Set the input width to 100%
                    />
                  </td>
                  <td>
                    {/* Add "Group By" text next to the checkmark */}
                    <label>
                      Group By:
                      <input
                        type="checkbox"
                        checked={groupByColumns[header]}
                        onChange={(e) => handleGroupByToggle(e, header)}
                      />
                    </label>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Section 4: Filter Section */}
      <div className="section section4">
        <div className="filter-table-container">
          <table className="filter-table">
            <thead>
              <tr>
                <th>Filter</th>
                <th>Column</th>
                <th>Criteria</th>
                <th>Search</th>
              </tr>
            </thead>
            <tbody>
              {headers.map((header) => (
                <tr key={header}>
                  <td>
                    <input
                      type="checkbox"
                      checked={!!filterCriteria[header]}
                      onChange={() =>
                        setFilterCriteria((prevCriteria) => ({
                          ...prevCriteria,
                          [header]: prevCriteria[header] ? "" : "All",
                        }))
                      }
                    />
                  </td>
                  <td>{renamedHeaders[header] || header}</td>
                  <td>
                    <input
                      type="text"
                      value={filterCriteria[header] || ""}
                      onChange={(e) =>
                        setFilterCriteria((prevCriteria) => ({
                          ...prevCriteria,
                          [header]: e.target.value,
                        }))
                      }
                      disabled={!filterCriteria[header]}
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      value={searchTerms[header] || ""}
                      onChange={(e) =>
                        setSearchTerms((prevSearchTerms) => ({
                          ...prevSearchTerms,
                          [header]: e.target.value,
                        }))
                      }
                    />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Section 5: CVS Data Display */}
      <div className="section section5">
        <div className="data-table-container">
          <DataTable
            columns={columns}
            data={groupedData}
            pagination={true}
            paginationPerPage={10}
            highlightOnHover={true}
            pointerOnHover={true}
            striped={true}
            onRowClicked={handleRowClick}
            fixedHeader={true}
            fixedHeaderScrollHeight="600px"
          />
        </div>
      </div>

      {/* Section 6: Modal */}
      <Modal
        isOpen={isModalOpen}
        onRequestClose={() => setIsModalOpen(false)}
        contentLabel="Row Details"
        className="modal"
        overlayClassName="overlay"
      >
        <RowPopup rowData={selectedRowData} />
        <button className="modal-close-button" onClick={() => setIsModalOpen(false)}>
          Close
        </button>
      </Modal>

      {/* Section 7: Download Buttons */}
      <div className="section section7">
        <button className="download-button" onClick={() => promptFileName("csv")}>
          Download CSV
        </button>
        <button className="download-button" onClick={() => promptFileName("json")}>
          Download JSON
        </button>
      </div>

      {/* Section 8: Reset Button */}
      <div className="section section8">
        <button className="reset-button" onClick={handleReset}>
          Reset
        </button>
      </div>

      {/* Section 9: Modal for File Name */}
      <Modal
        isOpen={isFileNameModalOpen}
        onRequestClose={() => setIsFileNameModalOpen(false)}
        contentLabel="Enter File Name"
        className="modal"
        overlayClassName="overlay"
      >
        <div>
          <h2>Enter File Name</h2>
          <input
            type="text"
            value={fileName}
            onChange={(e) => setFileName(e.target.value)}
            maxLength={100}
            style={{ width: "98%" }} // Set the input width to 100%
          />
          <button className="modal-close-button" onClick={handleFileNameSubmit}>
            Submit
          </button>
        </div>
      </Modal>
    </div>
  );
};

export default App;
