// utils.js

import Papa from "papaparse";

// Function to flatten the data based on selected columns
export const flattenData = (data, columns) => {
  const newData = data.map((row) => {
    const flattenedRow = {};
    columns.forEach((column) => {
      if (row[column] && typeof row[column] === "object") {
        Object.keys(row[column]).forEach((key) => {
          flattenedRow[`${column}.${key}`] = row[column][key];
        });
      } else {
        flattenedRow[column] = row[column];
      }
    });
    return flattenedRow;
  });
  return newData;
};

const flattenObject = (obj, parentKey = "") => {
  return Object.keys(obj).reduce((acc, key) => {
    const newKey = parentKey ? `${parentKey}.${key}` : key;
    const value = obj[key];

    if (typeof value === "object" && value !== null && !Array.isArray(value)) {
      const flattened = flattenObject(value, newKey);
      Object.assign(acc, flattened);
    } else {
      acc[newKey] = value;
    }

    return acc;
  }, {});
};

// Function to flatten the JSON data in specified columns
export const flattenJSONColumns = (data, columns) => {
  const newData = data.map((row) => {
    const flattenedRow = { ...row };
    columns.forEach((column) => {
      if (flattenedRow[column] && typeof flattenedRow[column] === "object") {
        const flattenedObject = flattenObject(flattenedRow[column]);
        delete flattenedRow[column];
        Object.assign(flattenedRow, flattenedObject);
      }
    });
    return flattenedRow;
  });

  return newData;
};

// Function to parse CSV data with special handling for JSON data in specified columns
export const parseCSVData = (fileContent, onComplete, onError) => {
  Papa.parse(fileContent, {
    header: true,
    skipEmptyLines: true,
    complete: (result) => {
      onComplete(result.data);
    },
    error: (error) => {
      onError(error);
    },
  });
};
